INSERT INTO `wp_posts` VALUES (80, 2, '2011-12-15 23:24:09', '2011-12-15 17:54:09', 'RIS has recently started developing plugins for the well known website creating software i.e WordPress.\n\nFor more information on WordPress, <a title="WordPress" href="http://wordpress.org/" target="_blank">click here</a>.\n\nRIS mostly develops security based plugins in order to keep your WordPress hosting safe and secure! MAC Dependent Cookies, DDOS-Defence, Copy-Safe are a few of our developments. Check their respective pages for more information regarding download/usage/working.\n\nCheck out Security Research,  if you are a fan of security, and want your hosting to be safe.\n\nIf you have any particular idea in mind, comment about it here. We will make a plugin for you - to make your WordPress installation secure!\n\nHelp us help you!\n\n&nbsp;', 'WordPress Security Plugins', '', 'inherit', 'open', 'open', '', '36-revision-2', '', '', '2011-12-15 23:24:09', '2011-12-15 17:54:09', '', 36, 'http://radsinnovativesolutions.com/?p=80', 0, 'revision', '', 0); 
